import React from "react";
import { Link } from "react-router-dom";
import "../styles/main.css";
import "../styles/forms.css";
import { CreateBankState } from '../types/Props'

class CreateBank extends React.Component<{}, CreateBankState> {
  constructor(props) {
    super(props);
    this.state = {
        bankID: '',
        bankName: '',
        street: '',
        city: '',
        state: '',
        zip: '',
        reservedAssets: 0,
        corpID: '',
        manager: '',
        bank_employee: ''
    };

    this.handlebankIDChange = this.handlebankIDChange.bind(this);
    this.handleLongNameChange = this.handleLongNameChange.bind(this);
    this.handleShortNameChange = this.handleShortNameChange.bind(this);
    this.handleReservedAssetsChange = this.handleReservedAssetsChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.clearState = this.clearState.bind(this);
  }

  handlebankIDChange(event) {
    this.setState({ bankID: event.target.value });
  }
  handleLongNameChange(event) {
    this.setState({ longName: event.target.value });
  }
  handleShortNameChange(event) {
    this.setState({ shortName: event.target.value });
  }
  handleReservedAssetsChange(event) {
    this.setState({ reservedAssets: event.target.value });
  }

  clearState(event) {
    console.log('cleared')
    this.setState({
        bankID: '',
        bankName: '',
        street: '',
        city: '',
        state: '',
        zip: '',
        reservedAssets: 0,
        corpID: '',
        manager: '',
        bank_employee: ''
    })
    event.preventDefault();

  }

  handleSubmit(event) {
    console.log("corporation created")
    console.log(    this.state.bankID + ", " 
                    + this.state.bankName + ", " 
                    + this.state.street + ", "
                    + this.state.city + ", "
                    + this.state.state + ", "
                    + this.state.zip + ", "
                    + this.state.reservedAssets + ", "
                    + this.state.corpID + ", "
                    + this.state.manager + ", "
                    + this.state.bank_employee  )
    this.clearState(event)
    event.preventDefault();
  }

  render() {
    return (
      <div className="container">
        <div className="mainHeader">
          <h6><Link to="../">Home</Link></h6>
          <h1>Q2: Create Bank</h1>
        </div>
        <div className="formContainer">
          <form onSubmit={this.handleSubmit}>
            <div className="formItem">
              <label>
                Bank ID:
              </label>
              <input type="text" value={this.state.bankID} onChange={this.handlebankIDChange} />
            </div>
            <div className="formItem">
              <label>
                Long Name:
              </label>
              <input type="text" value={this.state.longName} onChange={this.handleLongNameChange} />
            </div>
            <div className="formItem">
              <label>
                Short Name:
              </label>
              <input type="text" value={this.state.shortName} onChange={this.handleShortNameChange} />
            </div>
            <div className="formItem">
              <label>
                Reserved Assets:
              </label>
              <input type="text" value={this.state.reservedAssets} onChange={this.handleReservedAssetsChange} />
            </div>
            <button onClick={this.clearState} className="formCancel">
              Cancel
            </button>
            <button onClick={this.handleSubmit} className="formSubmit">
              Submit
            </button>
          </form>
        </div>
      </div>
    );
  }
}

export default CreateBank;
